 $.fn.transferN=function(e){if(document.getElementById(this.attr("id"))){var t="",n=$(this),r=n.clone();var i=$.extend({fly:true,targetUnit:"",speed:250},e);n.shadow=n.css("text-shadow");n.oldSize=n.css("font-size");n.oldValue=parseInt(n.text(),10);n.curValue=parseInt(n.text(),10);if(i.targetUnit!==""){i.targetUnit=$(i.targetUnit);i.targetVal=parseInt(i.targetUnit.text(),10)}r.attr("id","tempUnit000");if(n.oldValue>0&&!document.getElementById("tempUnit000")){$(this).off(event.type);t=setInterval(function(){r.css({position:"absolute",top:n.offset().top,left:n.offset().left,"font-size":n.oldSize,"text-shadow":n.shadow}).text(n.oldValue-n.curValue+1);n.curValue--;n.text(n.curValue);if(i.fly===true&&i.targetUnit!==""){$(r).appendTo("body").animate({top:i.targetUnit.offset().top,left:i.targetUnit.offset().left,"font-size":i.targetUnit.css("font-size")},{queue:false,duration:i.speed})}i.targetUnit!==""?i.targetUnit.text(i.targetVal+(n.oldValue-n.curValue)):"";if(n.curValue===0){clearInterval(t);r.remove()}},i.speed)}else{console.log("Can't transfer 0")}return this}else{alert("A single target element with a unique ID is required.")}}